mapper.select:
SELECT ia.*, io.name as username FROM iot_app ia INNER JOIN iot_app_org_relation iaor ON iaor.app_id=ia.id INNER JOIN iot_org io ON io.id =iaor.organization_id AND io.is_delete=1 WHERE ia.is_delete=1
AND (io.name LIKE "%"#{query}"%" or ia.app_eui LIKE "%"#{query}"%" or ia.name LIKE "%"#{query}"%" )
AND iaor.organization_id=#{orgId}
ORDER BY ia.create_time DESC LIMIT #{pageNumber}, #{pageSize}

SELECT count(ia.id) FROM iot_app ia INNER JOIN iot_app_org_relation iaor ON iaor.app_id=ia.id INNER JOIN iot_org io ON io.id =iaor.organization_id AND io.is_delete=1 WHERE ia.is_delete=1
AND (io.name LIKE "%"#{query}"%" or ia.app_eui LIKE "%"#{query}"%" or ia.name LIKE "%"#{query}"%" )
AND iaor.organization_id=#{orgId}
