mapper.select:
select d.id,d.device_uuid as 'deviceUuid', dev.name,dic.name as 'type', d.event_type as 'eventType', d.`status`, d.create_time as 'createTime',d.modify_time as 'modifyTime' from dev_data_warn d left join polycis_as_main.iot_device dev left join polycis_as_main.iot_dictionary dic on dic.code = dev.platform and dic.parent_id = 107 on d.device_uuid = dev.device_uuid INNER JOIN polycis_as_main.iot_app_org_relation iaor ON dev.app_id =iaor.app_id where 1 = 1
and d.mac like concat('%',#{data.mac},'%') or dev.name like concat('%',#{data.mac},'%')
and d.create_time >= #{data.beginTime}
and d.create_time <= #{data.endTime}
and d.status = #{data.status}
and iaor.organization_id like concat('%',#{data.id},'%') or dev.id like concat('%',#{data.id},'%')
order by d.create_time desc LIMIT #{key_offset},#{pageInfo.pageSize}

select count(*)
from dev_data_warn d
left join  polycis_as_main.iot_device dev on d.device_uuid = dev.device_uuid
INNER JOIN polycis_as_main.iot_app_org_relation iaor ON dev.app_id =iaor.app_id
where 1 = 1

    and d.mac like concat('%',#{data.mac},'%') or dev.name like concat('%',#{data.mac},'%')


    and d.create_time >= #{data.beginTime}


    and d.create_time <= #{data.endTime}


    and d.status = #{data.status}



    and iaor.organization_id like concat('%',#{data.id},'%') or dev.id like concat('%',#{data.id},'%')

select case when `status` = 0 then '未处理' when `status` = 2 then '已处理' end as 'label', count(id) as 'value' from dev_data_warn
where 1 = 1

    and create_time >= #{data.beginTime}


    and create_time <= #{data.endTime}

group by `status`
